const http = require('http')
const url = require('url')
const app = http.createServer((req, res) => {
    console.log(req.url, 'requesUrl')
    // console.log("hello")
    let newUrl =url.parse(req.url, true);
    console.log(newUrl,'')
    if(newUrl.pathname==='/'){
        res.end("home page ")
    }
    else if(newUrl.pathname==='/contact'){
        res.end("contact page  ")
    }
    else if(newUrl.pathname==='/about'){
        res.end("about page  ")
    }
    else if(newUrl.pathname==='/simpleintrest'){
        let principal = newUrl.query.p;
        let rate =newUrl.query.r;
        let time =newUrl.query.t;
        let si =(principal*rate*time)/100;
        console.log(si);
        res.end(`simple intrest ${si}`);
    }

});
app.listen(3000, (err) => {
    if (err) {
        console.log("problem ")
    } else {

        console.log("server created sucessfully")
        console.log("server running on port 3000")
    }
})